import React from 'react';
import { PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const data = [
  { name: 'Product A', value: 4000 },
  { name: 'Product B', value: 3000 },
  { name: 'Product C', value: 2500 },
  { name: 'Product D', value: 1500 },
];

const COLORS = ['#2929c5', '#13ad4c', '#ffc658', '#ff7300'];

function PieCharts() {
  return (
    <div className="p-4 bg-white rounded-xl shadow-lg">
      <h2 className="text-xl font-bold mb-4 text-center text-gray-800">Product Share</h2>
      
     
      <ResponsiveContainer width={1000} height={300}>
        <PieChart>
          <Pie
            data={data}
            dataKey="value"
            nameKey="name"  
            // cx="50%"        
            // cy="50%"        
            outerRadius={100} 
            fill="#212030ff"
            // paddingAngle={0} 
          >
            
            {data.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>

          
          <Tooltip />

          
          <Legend layout="horizontal" verticalAlign="bottom" align="center" />
        </PieChart>
      </ResponsiveContainer>
    </div>
  );
}

export default PieCharts;
